import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import Question from './Question';
import TextAnswer from './SurveyAnswer/TextAnswer';
import AddressAnswer from './SurveyAnswer/AddressAnswer';
import MultChoiceAnswer from './SurveyAnswer/MultChoiceAnswer';
import Question412 from './SurveyAnswer/Question4.1-2';
import Question1 from './Questions/Question1';
import Question1_1 from './Questions/Question1_1';
import Question2 from './Questions/Question2';

const nextQuestions =[

];

const answers = {

};

class FormV2 extends Component {
   
    constructor(props) {
        super(props)
    
        this.state = {
            currentQuestion: "q1",
            user: null,
            question3: ""
        };
    }
    
    // handleLastQuestionBtn = () => {
    //     const {currentQuestionIndex} = this.state;
        
    //     this.setState({
    //         currentQuestionIndex: currentQuestionIndex - 1,
    //         currentAnswerValue: surveyQuestions[currentQuestionIndex-1].defaultAnswerValue
    //     })
    // }

    // onSubmit = (e) => {
    //     e.preventDefault();


    //     const {currentQuestionIndex, currentAnswerValue} = this.state;

    //     surveyQuestions[currentQuestionIndex].defaultAnswerValue = currentAnswerValue;
    //     console.log(currentQuestionIndex, currentAnswerValue);

    //     if (currentQuestionIndex === surveyQuestions.length - 1) {
    //         console.log("Survey complete");
    //     } else {
    //         this.setState({
    //             currentQuestionIndex: currentQuestionIndex + 1,
    //             currentAnswerValue: surveyQuestions[currentQuestionIndex + 1].defaultAnswerValue
    //         });
    //     }
    // }
    
    onQuestionSubmit = (questionName, answer, nextQs) => {
        answers[questionName] = answer;

        nextQuestions.shift();
        nextQuestions.push(...nextQs);

        this.setState({
            currentQuestion: nextQuestions[0]
        })
        console.log(questionName, answer);
        console.dir(nextQuestions);
    }
   
    render() {
       
        const {currentQuestionIndex} = this.state;
        // const currentQuestion = surveyQuestions[currentQuestionIndex];
        
        return (
            <div>
                
                <div>
                    <h1>Form v2</h1>
                </div>

                <div>
                <form onSubmit={this.onSubmit}>
                    
                    <h5>Question {currentQuestionIndex + 1}</h5>

                    {this.state.currentQuestion === "q1" && (
                        <Question1 onSubmit={this.onQuestionSubmit}></Question1>
                    )}
                    
                    
                    {this.state.currentQuestion === "q1.1" && (
                        <Question1_1 onSubmit={this.onQuestionSubmit}></Question1_1>
                    )}

                    {this.state.currentQuestion === "q2" && (
                        <Question2 onSubmit={this.onQuestionSubmit}></Question2>
                    )}

          
                    <button type="button" id="last-question-btn" onClick={this.handleLastQuestionBtn}>Go Back</button>

                </form>
                </div>
            </div>
        )
    }
}

export default FormV2
