import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import {BrowserRouter as Router, Switch, Route, Redirect} from 'react-router-dom';
import Survey from './Survey';
import Form from './Form';

class Home extends Component {
    
    constructor(props) {
        super(props)
    
        this.state = {
             isLoading: true,
             user: null,
             error: null
        }
    }
    

    
    render() {

        if(this.state.isLoading){
            return(
                <div>Loading...</div>
            )
        }

        return (
            <div>
                <h1>Homepage</h1>
                <Link to='/login'>
                    <div>Login</div>
                </Link>
                <Link to='/register'>
                    <div>Register</div>
                </Link>
                <Link to='/passwordreset'>
                    <div>Forgot Password?</div>
                </Link>
                <Link to='/home/survey'>
                    <div>Survey</div>
                </Link>
                <Link to='/home/form'>
                    <div>Form</div>
                </Link>
                <div>
                    <Switch>
                        <Route path="/home/survey" exact component={Survey}></Route>
                        <Route path="/home/form" exact component={Form}></Route>
                    </Switch>
                </div>
            </div>
        )
    }
}

export default Home
