import React, { Component } from 'react'

class Question2 extends Component {
    
    constructor(props) {
        super(props)
    
        this.state = {
            questionNum: 2,
            questionContent: "Where do you currently reside?",
            answerType: "text",
            defaultAnswerValue: ""
        }
    }
    
    
    renderQuestion = (questionString) =>{
        return questionString;
    }
    
    onSubmit = (event) =>{
        const {currentAnswerValue} = this.state;
        event.preventDefault();

        const nextQuestions = [];

        if(!currentAnswerValue){
            return;
        }

        this.props.onSubmit("q2",currentAnswerValue, nextQuestions);
    }

    render() {

        return (
            <div>
                <div>
                    {this.renderQuestion("<----TEST POST FOLLOW UP QUESTION---->")}
                </div>
                <div>
                    <input></input>
                </div>
                <div>
                    <button type="submit" className="next-question-btn" onClick={this.onSubmit}>Next Question</button>
                </div>
            </div>
        )
    }
}

export default Question2
