import React, { Component } from 'react'

class TextAnswer extends Component {
    
    constructor(props) {
        super(props)
    
        this.state = {
             valueOfAnswer: "",
        }
    }
    

    render() {
        return (
            <div>
                <div>
                    <label htmlFor="textanswer">{this.props.answerLabel}</label>
                    <input id="textanswer" type="text" value={this.state.valueOfAnswer} onChange={this.props.handleResponse}></input>
                </div>
            </div>
        )
    }
}

export default TextAnswer
