import React, { Component } from 'react'

class Question412 extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
            marriageDate: props.value.marriageDate,
            marriagePlace: props.value.marriagePlace,
            separationDate: props.value.separationDate,
        }
    }
    
    onMarriageDateChange = (event) => {
        this.setState({
            marriageDate: event.target.value
        },() => this.props.handleQ412Response(this.state))
    }

    onMarriagePlaceCityChange = (event) => {
        this.setState({
            marriagePlace: event.target.value
        },() => this.props.handleQ412Response(this.state))
    }

    onMarriagePlaceCountyChange = (event) => {
        this.setState({
            marriagePlace: event.target.value
        },() => this.props.handleQ412Response(this.state))
    }

    onMarriagePlaceStateChange = (event) => {
        this.setState({
            marriagePlace: event.target.value
        },() => this.props.handleQ412Response(this.state))
    }

    onSeparationDateChange = (event) => {
        this.setState({
            separationDate: event.target.value
        },() => this.props.handleQ412Response(this.state))
    }


    render() {
        return (
            <div>
                <div className="q412container">
                    <label htmlFor="marriagedate" className="q412" >On what date were you married?</label>
                    <input className="marriagedate"  value={this.props.value.marriageDate} type="text" onInput={this.onMarriageDateChange}></input>

                    <label htmlFor="marriageplace" className="q412" >Where were you married?</label>
                    <div>
                        <label htmlFor="marriageplacecity" className="q412" >City</label>
                        <input className="marriageplacecity" value={this.props.value.marriagePlace} type="text" onInput={this.onMarriagePlaceCityChange}></input>
                        <label htmlFor="marriageplacecounty" className="q412" >County</label>
                        <input className="marriageplacecounty" value={this.props.value.marriagePlace} type="text" onInput={this.onMarriagePlaceCountyChange}></input>
                        <label htmlFor="marriageplacestate" className="q412" >State</label>
                        <input className="marriageplacestate" value={this.props.value.marriagePlace} type="text" onInput={this.onMarriagePlaceStateChange}></input>
                    </div>

                    <label htmlFor="separationdate" className="q412" >Date of Separation:</label>
                    <input className="separationdate"  value={this.props.value.separationDate} type="text" onInput={this.onSeparationDateChange}></input>

                </div>
            </div>
        )
    }
}

export default Question412
