import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import Question from './Question';
import TextAnswer from './SurveyAnswer/TextAnswer';
import AddressAnswer from './SurveyAnswer/AddressAnswer';
import MultChoiceAnswer from './SurveyAnswer/MultChoiceAnswer';
import NextButton from './NextButton';

const surveyQuestions = [
    {
        questionContent: "Where do you currently reside?",
        answerType: "text",
        defaultAnswerValue: ""
    },
    {
        questionContent: "Please enter your residence information",
        answerType: "address",
        defaultAnswerValue: {
            Address: "",
            City: "",
            County: "",
            State: ""
        }
    },
    {
        questionContent: "Have any papers been filed in this matter?",
        answerType: "multchoice",
        defaultAnswerValue: ""
    },
    {
        questionContent: "blah",
        answerType: "text",
        defaultAnswerValue: ""
    }
];

const surveyAnswers = [
    
]

class Survey extends Component {
    
    constructor(props) {
        super(props)
    
        this.state = {
            currentQuestionIndex: 0,
            currentAnswerValue: surveyQuestions[0].defaultAnswerValue,
        };
    }

    handleLastQuestionBtn = () => {
        const {currentQuestionIndex, currentAnswerValue} = this.state;
        
        this.setState({
            currentQuestionIndex: currentQuestionIndex - 1,
            currentAnswerValue: surveyQuestions[currentQuestionIndex-1].defaultAnswerValue
        })
    }

    handleAnswerValueChange = (val) => {
        this.setState({currentAnswerValue: val});
    }

    onSubmit = (e) => {
        e.preventDefault();


        const {currentQuestionIndex, currentAnswerValue} = this.state;

        surveyQuestions[currentQuestionIndex].defaultAnswerValue = currentAnswerValue;
        console.log(currentQuestionIndex, currentAnswerValue);

        if (currentQuestionIndex === surveyQuestions.length - 1) {
            console.log("Survey complete");
        } else {
            this.setState({
                currentQuestionIndex: currentQuestionIndex + 1,
                currentAnswerValue: surveyQuestions[currentQuestionIndex + 1].defaultAnswerValue
            });
        }
    }

    render() {
        const {currentQuestionIndex} = this.state;
        const currentQuestion = surveyQuestions[currentQuestionIndex];

        return (
            <div>
                <div>
                    <h1>Survey</h1>
                </div>
                
                <Link to='/homepage'>
                    <div>Home</div>
                </Link>

                {this.state.user && (
                        <div>{this.state.user.name}</div>
                    )}


                <form onSubmit={this.onSubmit}>
                    
                    <h5>Question {currentQuestionIndex + 1}</h5>
                    <Question id={`question-${currentQuestionIndex}`} content={currentQuestion.questionContent}></Question>


                    {currentQuestion.answerType === "text" && (
                        <TextAnswer value={this.state.currentAnswerValue} handleTextResponse={this.handleAnswerValueChange} answerLabel={currentQuestion.questionContent}></TextAnswer>
                    )}
                    
                    {currentQuestion.answerType === "address" && (
                         <AddressAnswer value={this.state.currentAnswerValue} handleAddressResponse={this.handleAnswerValueChange}></AddressAnswer>
                    )}

                    {currentQuestion.answerType === "multchoice"&& currentQuestionIndex === 2 && (
                        <div>
                            <MultChoiceAnswer value="YES" text="YES"  handleMultChoiceResponse={this.handleAnswerValueChange}></MultChoiceAnswer>
                            <MultChoiceAnswer value="NO" text= "NO"  handleMultChoiceResponse={this.handleAnswerValueChange}></MultChoiceAnswer>
                        </div>
                    )}
                    <button type="button" id="last-question-btn" onClick={this.handleLastQuestionBtn}>Go Back</button>
                    <button type="submit" id="next-question-btn">Next Question</button>
                </form>
            </div>
        )
    }
}

export default Survey
