import React, { Component } from 'react'

let addClientFields = {
    "nameField": "",
    "addressField": "",
};

let inputFields = [];

let test2d = [
    ["Sawyer", "123", "Sherlock"],
    ["Mack", "543", "Arizone"],
    ["Ty", "785", "Wash"],
    ["Andrew", "243", "Franc"]
];

let temp1darray = [];

class ClientListAttempt2 extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             counter: 0
        }
    }

    sort2dArr = (a, b) => {
        if (a[0] === b[0]) {
            return 0;
        }
        else {
            return (a[0] < b[0]) ? -1 : 1;
        }
    }

    handleOnBlur = (event) => {
        this.setState({
            counter: this.state.counter + 1
        })
    }


    insertInfo = (event, inputType) => {

        let counter = test2d.length;
        let k = 'value';

        var i;
        for(i = 1; i < 5; i++) {
        window['value'+i] = + i;
    }
        console.log(value1);

        switch(inputType){
            case "name":
                inputFields[0] = (event.target.value);
                break;
            case "address":
                inputFields[1] = (event.target.value);
                break;
            case "city":
                inputFields[2] = (event.target.value);
                break;
            case "telephone":
                inputFields[3] = (event.target.value);
                break;
            case "email":
                inputFields[4] = (event.target.value);
                break;
            case "map":
                return (inputFields);
        }
    }

    deepCopy = (arr) => {
        let copy = [];
        arr.forEach(elem => {
          if(Array.isArray(elem)){
            copy.push(this.deepCopy(elem))
          }else{
            if (typeof elem === 'object') {
              copy.push(this.deepCopyObject(elem))
          } else {
              copy.push(elem)
            }
          }
        })
        return copy;
      }
      // Helper function to deal with Objects
    deepCopyObject = (obj) => {
        let tempObj = {};
        for (let [key, value] of Object.entries(obj)) {
          if (Array.isArray(value)) {
            tempObj[key] = this.deepCopy(value);
          } else {
            if (typeof value === 'object') {
              tempObj[key] = this.deepCopyObject(value);
            } else {
              tempObj[key] = value
            }
          }
        }
        return tempObj;
      }

    handleNameInput = (event) => {
        let name = "name";
        this.insertInfo(event, name);
    }
    handleAddressInput = (event) => {
        let address = "address";
        this.insertInfo(event, address);
    }
    handleCityInput = (event) => {
        let city = "city";
        this.insertInfo(event, city);
    }
    handleTelephoneInput = (event) => {
        let telephone = "telephone";
        this.insertInfo(event, telephone);
    }
    handleEmailInput = (event) => {
        let email = "email";
        this.insertInfo(event, email);
    }

    handleNewClientBtnPress = (event) => {
        // temp1darray.push(addClientFields["addressField"]);
        let newArr = this.deepCopy(test2d);
        console.log(newArr);
        let map = "map"
        let tempArray = this.insertInfo(event, map)
        newArr.push(tempArray);
        newArr.sort(this.sort2dArr);
        //SUCCESSFUL sorting of 2d array
        console.log(newArr);

        this.setState({
            counter: this.state.counter + 1,
        })
    }

    render() {
        return (
            <div>
                <div className="addclient-container">
                    <div className="label-input-container">
                        <label>Name</label>
                        <input type="text" className="addclient-input-fields" id="addclient-name-input" onInput = {this.handleNameInput} onBlur= {this.handleOnBlur}></input>
                    </div>
                    <div className="label-input-container">
                                <label>Street Address</label>
                                <input type="text" className="addclient-input-fields" id="addclient-address-input" onChange = {this.handleAddressInput} onBlur= {this.handleOnBlur}></input>
                            </div>
                            <div className="label-input-container">
                                <label>City</label>
                                <input type="text" className="addclient-input-fields" id="addclient-city-input" onChange = {this.handleCityInput} onBlur= {this.handleOnBlur}></input>
                            </div>
                            <div className="label-input-container">
                                <label>Telephone Number</label>
                                <input type="text" className="addclient-input-fields" id="addclient-phone-input" onChange = {this.handleTelephoneInput} onBlur= {this.handleOnBlur}></input>
                            </div>
                            <div className="label-input-container">
                                <label>Email Address</label>
                                <input type="text" className="addclient-input-fields" id="addclient-email-input" onChange = {this.handleEmailInput} onBlur= {this.handleOnBlur}></input>
                            </div>
                    <div>
                        <button onClick = {this.handleNewClientBtnPress}>click</button>
                    </div>
                </div>
                <div className="ui-database-container">
                    <div className="ui-database-row-container">
                        
                    </div>
                </div>
            </div>
        )
    }
}

export default ClientListAttempt2
