import React, { Component } from 'react'

class Register extends Component {
    
    constructor(props) {
        super(props)
    
        this.state = {
             emailField: "",
             passwordField: "",
             errorMessage: ""
        }
    }
    
    handleEmailRegistration = (event) => {
        this.setState({
            emailField: event.target.value
        })
    }

    handlePasswordRegistration = (event) => {
        this.setState({
            passwordField: event.target.value
        })
    }
    
    handleRegistration = (event) => {
        event.preventDefault();
        console.log(`${this.state.emailField} ${this.state.passwordField}`);

        this.setState({
            errorMessage: ""
        })

        const {emailField, passwordField, errorMessage} = this.state;
        
        const registrationRequestBody = {
            emailField: emailField,
            passwordField: passwordField,
            errorMessage: errorMessage
        };

        console.log(registrationRequestBody);

        fetch("http://127.0.0.1:3001/register", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(registrationRequestBody) 
        }).then((res) => {
            return res.json();
        }).then((parsedResponse) => {
            console.log(parsedResponse);
        })
    }

    render() {
        return (
            <div>
                <h1>Register A New Account</h1>
                <div>
                {this.state.errorMessage.length > 0 && (
                    <div className="errordiv">
                        {this.state.errorMessage}
                    </div>
                )}
                </div>
                <form onSubmit={this.handleRegistration}>
                    {/* Email Registration */}
                    <div>
                        <label htmlFor="registeremailinput" className = "form-labels">Email Address</label>
                        <input id="registeremailinput" type="text" value={this.state.emailField} onChange = {this.handleEmailRegistration}></input>
                    </div>
                    
                    {/* Password Registration */}
                    <div>
                        <label htmlFor ="registerpassword" className = "form-labels">Password</label>
                        <input id="registerpassword" type="password" value={this.state.passwordField} onChange={this.handlePasswordRegistration}></input>
                    </div>
                    
                    {/* Submit Button */}
                    <div>
                        <button type="submit">Submit</button>
                    </div>
                </form>
            </div>
        )
    }
}

export default Register
