import React, { Component } from 'react'

// 1: Add element HTML
// 2: Assign Component State to element value
// 3: Assign onchange handler that updates the state


class Form extends Component {
    
    constructor(props) {
        super(props)
    
        this.state = {
             email: "",
             password: "",
             topic: "student"
        }
    }
    
    handleEmailChange = (event) => {
        this.setState({
            email: event.target.value
        })
    }
    
    handlePasswordChange = (event) => {
        this.setState({
            password: event.target.value

        })
    }

    handleTopicChange = (event) => {
        this.setState({
            topic: event.target.value
        })
    }

    handleSubmit = (event) => {
        event.preventDefault();
        console.log(`${this.state.email} ${this.state.password} ${this.state.topic}`)
    }
  
    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    
                    {/* Email Input */}
                    <div>
                        <label htmlFor="emailinput" className = "form-labels">Email Address</label>
                        <input id="emailinput" type="email" value={this.state.email} onChange = {this.handleEmailChange}></input>
                    </div>
                    
                    {/* Password Input */}
                    <div>
                        <label htmlFor ="textarea" className = "form-labels">Password</label>
                        <input id="textarea" type="password" value={this.state.password} onChange={this.handlePasswordChange}></input>
                    </div>
                    
                    {/* Option Input */}
                    <div>
                        <label htmlFor="optionstuff" className = "form-labels">Topic</label>
                        <select id="optionstuff" value={this.state.topic} onChange={this.handleTopicChange}>
                            <option value = "student">Student</option>
                            <option value = "teacher">Teacher</option>
                            <option value = "other">Other</option>
                        </select>
                    </div>
                    
                    {/* Submit Button */}
                    <div>
                        <button type="submit">Submit</button>
                    </div>

                </form>
            </div>
        )
    }
}

export default Form
