    }

    render() {
        return (
            <div>
                <div>
                    <h1>Survey</h1>
                </div>

                <Link to='/homepage'>
                    <div>Home</div>
                </Link>

                {/* QUESTION 1 */}
                <h5>Question 1</h5>
                <Question id="question-1" content={this.state.currentQuestion.questionContent}> </Question>

                {this.state.currentQuestion.answerType === "address" && (
                    <div>todo address component</div>
                )}

                {this.state.currentQuestion.answerType === "text" && (
                    <TextAnswer answerLabel="Address" value={this.state.question1AddressValue} handleTextResponse={this.handleQuestion1Address}></TextAnswer>
                )}

                <TextAnswer answerLabel="Address" value={this.state.question1AddressValue} handleTextResponse={this.handleQuestion1Address}></TextAnswer>
                <TextAnswer answerLabel="City" value={this.state.question1CityValue} handleTextResponse={this.handleQuestion1City}></TextAnswer>
                <TextAnswer answerLabel="County" value={this.state.question1CountyValue} handleTextResponse={this.handleQuestion1County}></TextAnswer>
                <TextAnswer answerLabel="State" value={this.state.question1StateValue} handleTextResponse={this.handleQuestion1State}></TextAnswer>
                <NextButton for="question1" handleNextButton={this.handleNextButtonQ1}></NextButton>

                {this.state.nextButtonQ1Pressed && (
                <div> 
                    <Question id="question-2" content=""></Question>
                    <MultChoiceAnswer handleMultChoiceResponse={this.handleQuestion2Option1}></MultChoiceAnswer>
                </div>   
                )}
                
            </div>






        )
    }
}

export default Survey
