import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import Question from './Question';
import TextAnswer from './TextAnswer';

class Survey extends Component {
    
    constructor(props) {
        super(props)
    
        this.state = {
            question1AddressValue: "",
            question1CityValue: ""
        }
    }

    handleQuestion1Address = (event) => {
        this.setState({
            question1AddressValue: event.target.value
        })
    }

    handleQuestion1City = (event) => {
        this.setState({
            question1CityValue: event.target.value
        })
    }

    
    render() {
        return (
            <div>
                <div>
                    <h1>Survey</h1>
                </div>

                <Link to='/homepage'>
                    <div>Home</div>
                </Link>

                <Question id="question-1" content="Where do you currently reside?"></Question>
                <TextAnswer answerLabel="Address" value={this.state.question1AddressValue} handleResponse={this.handleQuestion1Address}></TextAnswer>
                <TextAnswer answerLabel="City" value={this.state.question1CityValue} handleResponse={this.handleQuestion1City} ></TextAnswer>
                <TextAnswer answerLabel="County" handleResponse=""></TextAnswer>
                <TextAnswer answerLabel="State" handleResponse=""></TextAnswer>

                <Question id="question-2" content=""></Question>
            </div>
        )
    }
}

export default Survey
