import React, { Component } from 'react'
import { Link, Redirect } from 'react-router-dom';

class Register extends Component {
    
    constructor(props) {
        super(props)
    
        this.state = {
             emailField: "",
             passwordField: "",
             nameField: "",
             isRegistered: false,
             errorMessage: ""
        }
    }
    
    handleEmailRegistration = (event) => {
        this.setState({
            emailField: event.target.value
        })
    }

    handleNameRegistration = (event) => {
        this.setState({
            nameField: event.target.value
        })
    }

    handlePasswordRegistration = (event) => {
        this.setState({
            passwordField: event.target.value
        })
    }
    
    handleRegistration = (event) => {
        event.preventDefault();
        console.log(`${this.state.emailField} ${this.state.passwordField} ${this.state.nameField}`);

        this.setState({
            errorMessage: ""
        })

        const {emailField, passwordField, nameField, errorMessage} = this.state;
        
        const registrationRequestBody = {
            emailField: emailField,
            passwordField: passwordField,
            nameField: nameField
        };

        console.log(registrationRequestBody);

        fetch("http://127.0.0.1:3001/register", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(registrationRequestBody) 
        }).then((res) => {
            return res.json();
        }).then((parsedResponse) => {
            console.log(parsedResponse);
            if(parsedResponse.errorInEmail.length > 0){
                this.setState({
                    errorMessage: parsedResponse.errorInEmail
                })
            }
            else{
                this.setState({
                    isRegistered: true
                })
            }
        })
    }

    render() {
        return (
            <div>
                <h1>Register A New Account</h1>
                <div>
                    {this.state.errorMessage}
                </div>
                <form onSubmit={this.handleRegistration}>
                    {/* Email Registration */}
                    <div>
                        <label htmlFor="registeremailinput" className = "form-labels">Email Address</label>
                        <input id="registeremailinput" type="text" value={this.state.emailField} onChange = {this.handleEmailRegistration}></input>
                    </div>
                    
                    {/* Name Registration */}
                    <div>
                        <label htmlFor="registernameinput" className = "form-labels">Name</label>
                        <input id="registernameinput" type="text" value={this.state.nameField} onChange = {this.handleNameRegistration}></input>
                    </div>

                    {/* Password Registration */}
                    <div>
                        <label htmlFor ="registerpassword" className = "form-labels">Password</label>
                        <input id="registerpassword" type="password" value={this.state.passwordField} onChange={this.handlePasswordRegistration}></input>
                    </div>
                    
                    {/* Submit Button */}
                    <div>
                        <button type="submit">Submit</button>
                    </div>
                </form>
                <Link to='/homepage'>
                    <div>Home</div>
                </Link>
                {this.state.isRegistered && (
                    <Redirect to="/homepage"></Redirect>
                )}  
            </div>
        )
    }
}

export default Register
