import React, { Component } from 'react'

// 1: Add element HTML
// 2: Assign Component State to element value
// 3: Assign onchange handler that updates the state



class Form extends Component {
    
    constructor(props) {
        super(props)
    
        this.state = {
             username: "",
             comments: "",
             topic: "react"
        }
    }
    
    handleUsernameChange = (event) => {
        this.setState({
            username: event.target.value
        })
    }
    
    handleCommentsChange = (event) => {
        this.setState({
            comments: event.target.value
        })
    }

    handleTopicChange = (event) => {
        this.setState({
            topic: event.target.value
        })
    }
  
    render() {
        return (
            <div>
                <form>
                    <div>
                        <label>Username</label>
                        <input type="text" value={this.state.username} onChange = {this.handleUsernameChange}></input>
                    </div>
                    <div>
                        <label htmlFor ="textarea">Comments</label>
                        <textarea id="textarea" value={this.state.comments} onChange={this.handleCommentsChange}></textarea>
                    </div>
                    <div>
                        <label htmlFor="optionstuff">Topic</label>
                        <select id="optionstuff" value={this.state.topic} onChange={this.handleTopicChange}>
                            <option value = "react">React</option>
                            <option value = "angular">Angular</option>
                            <option value = "vue">Vue</option>
                        </select>
                    </div>

                </form>
            </div>
        )
    }
}

export default Form
