import React, { Component } from 'react'

class Question1 extends Component {
    
    constructor(props) {
        super(props)
    
        this.state = {
            currentAnswerValue: ""
        }
    }
    
    
    renderQuestion = (questionString) =>{
        return questionString;
    }
    
    onOption1 = (event) => {
        event.preventDefault();
        
        this.setState({
            currentAnswerValue: "YES"
        })
        

    }

    onOption2 = (event) => {
        event.preventDefault();
        
        this.setState({
            currentAnswerValue: "NO"
        })

    }

    onSubmit = (event) =>{
        const {currentAnswerValue} = this.state;
        event.preventDefault();

        const nextQuestions = [];

        if(!currentAnswerValue){
            return;
        }
        
        if(currentAnswerValue === "YES"){
            nextQuestions.push("q1.1")
        }

        nextQuestions.push("q2")
        this.props.onSubmit("q1",currentAnswerValue, nextQuestions);
    }

    render() {
        const option1background = (this.state.currentAnswerValue === "YES") ? "green" : "";
        const option2background = (this.state.currentAnswerValue === "NO") ? "green" : "";
        return (
            <div>
                <div>
                    {this.renderQuestion("Have any papers been filed in this matter?")}
                </div>
                <div>
                    <button className="q1-option1" style={{backgroundColor: option1background}} value="YES" onClick={this.onOption1}>YES</button>
                    <button className="q1-option2" style={{backgroundColor: this.state.backgroundcoloroption2}} value="NO" onClick={this.onOption2}>NO</button>
                </div>
                <div>
                    <button type="submit" id="next-question-btn" onClick={this.onSubmit}>Next Question</button>
                </div>
            </div>
        )
    }
}

export default Question1
