import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import Question from './Question';
import TextAnswer from './TextAnswer';
import MultChoiceAnswer from './MultChoiceAnswer';

class Survey extends Component {
    
    constructor(props) {
        super(props)
    
        this.state = {
            question1AddressValue: "",
            question1CityValue: "",
            question1CountyValue: "",
            question1StateValue: ""
        }
    }

    handleQuestion1Address = (event) => {
        this.setState({
            question1AddressValue: event.target.value
        })
        const userAddress = this.state.question1AddressValue;
    }

    handleQuestion1City = (event) => {
        this.setState({
            question1CityValue: event.target.value
        })
    }

    handleQuestion1County = (event) => {
        this.setState({
            question1CountyValue: event.target.value
        })
    }

    handleQuestion1State = (event) => {
        this.setState({
            question1StateValue: event.target.value
        })
    }
    
    handleQuestion2Option1 = (event) => {
        console.log("worked");
    }

    render() {
        return (
            <div>
                <div>
                    <h1>Survey</h1>
                </div>

                <Link to='/homepage'>
                    <div>Home</div>
                </Link>

                <Question id="question-1" content="Where do you currently reside?"></Question>
                <TextAnswer answerLabel="Address" value={this.state.question1AddressValue} handleTextResponse={this.handleQuestion1Address}></TextAnswer>
                <TextAnswer answerLabel="City" value={this.state.question1CityValue} handleTextResponse={this.handleQuestion1City}></TextAnswer>
                <TextAnswer answerLabel="County" value={this.state.question1CountyValue} handleTextResponse={this.handleQuestion1County}></TextAnswer>
                <TextAnswer answerLabel="State" value={this.state.question1StateValue} handleTextResponse={this.handleQuestion1State}></TextAnswer>

                {/* <Question id="question-2" content=""></Question>
                <MultChoiceAnswer handleMultChoiceResponse={this.handleQuestion2Option1}></MultChoiceAnswer> */}
            </div>
        )
    }
}

export default Survey
