import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import Question from './Question';
import TextAnswer from './SurveyAnswer/TextAnswer';
import MultChoiceAnswer from './SurveyAnswer/MultChoiceAnswer';
import NextButton from './NextButton';

const surveyQuestions = [
    {
        questionContent: "Where do you currently reside?",
        answerType: "text"
    },
    {
        questionContent: "What's your favorite ice cream?",
        answerType: "text"
    }
];


class Survey extends Component {
    
    constructor(props) {
        super(props)
    
        this.state = {
            currentQuestionIndex: 0,
            currentAnswerValue: ""
        };

        this.handleAnswerValueChange = this.handleAnswerValueChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }

    handleAnswerValueChange(val) {
        this.setState({currentAnswerValue: val});
    }

    onSubmit(e) {
        e.preventDefault();

        const {currentQuestionIndex, currentAnswerValue} = this.state;

        console.log(currentQuestionIndex, currentAnswerValue);

        if (currentQuestionIndex == surveyQuestions.length - 1) {
            console.log("Survey complete");
        } else {
            this.setState({
                currentQuestionIndex: currentQuestionIndex + 1,
                currentAnswerValue: ""
            });
        }
    }

    render() {
        const {currentQuestionIndex} = this.state;
        const currentQuestion = surveyQuestions[currentQuestionIndex];

        return (
            <div>
                <div>
                    <h1>Survey</h1>
                </div>

                <Link to='/homepage'>
                    <div>Home</div>
                </Link>

                <form onSubmit={this.onSubmit}>
                    <h5>Question {currentQuestionIndex + 1}</h5>
                    <Question id={`question-${currentQuestionIndex}`} content={currentQuestion.questionContent}></Question>
                    
                    {currentQuestion.answerType === "text" && (
                        <TextAnswer value={this.state.currentAnswerValue} handleTextResponse={this.handleAnswerValueChange} answerLabel={currentQuestion.questionContent}></TextAnswer>
                    )}

                    <button type="submit">Next Question</button>
                </form>
            </div>
        )
    }
}

export default Survey
