import React, { Component } from 'react'
 
class Address extends Component {
    
    constructor(props) {
        super(props)
    
        this.state = {
             Address: "Address",
             City: "City",
             County: "County",
             State: "State"
        }

        this.onAddressChange = this.onAddressChange.bind(this);

    }
    
    onAddressChange(e) {
        this.props.handleTextResponse(e.target.value);
    }

    onCityChange(e) {
        this.props.handleTextResponse(e.target.value);
    }

    onCountyChange(e) {
        this.props.handleTextResponse(e.target.value);
    }

    onStateChange(e) {
        this.props.handleTextResponse(e.target.value);
    }
    
    render() {
        return (
            <div>
                <div>
                    <label htmlFor="addressanswer">{this.state.Address}</label>
                    <input className="addressanswer" type="text" onInput={this.onAddressChange}></input>

                    <label htmlFor="cityanswer">{this.state.City}</label>
                    <input className="cityanswer" type="text" onInput={this.onCityChange}></input>

                    <label htmlFor="countyanswer">{this.state.County}</label>
                    <input className="countyanswer" type="text" onInput={this.onCountyChange}></input>

                    <label htmlFor="stateanswer">{this.state.State}</label>
                    <input className="stateanswer" type="text" onInput={this.onStateChange}></input>
                </div>
            </div>
        )
    }
}

export default Address
