import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import Question from './Question';
import TextAnswer from './SurveyAnswer/TextAnswer';
import MultChoiceAnswer from './SurveyAnswer/MultChoiceAnswer';
import NextButton from './NextButton';

const surveyQuestions = [
    {
        questionContent: "Where do you currently reside",
        answerType: "address"
    }
];


class Survey extends Component {
    
    constructor(props) {
        super(props)
    
        this.state = {
            currentQuestionIndex: 0
        };
    }

    OnSubmit(value) {

    }

    render() {
        const currentQuestion = surveyQuestions[this.state.currentQuestionIndex];

        return (
            <div>
                <div>
                    <h1>Survey</h1>
                </div>

                <Link to='/homepage'>
                    <div>Home</div>
                </Link>

                <form onSubmit={this.OnSubmit}>
                    <h5>Question {this.state.currentQuestionIndex + 1}</h5>
                    <Question id="question-1" content={currentQuestion.questionContent}> </Question>

                </form>

                {/* QUESTION 1 */}
                <h5>Question 1</h5>
                <Question id="question-1" content={currentQuestion.questionContent}> </Question>

                {this.state.currentQuestion.answerType === "address" && (
                    <div>todo address component</div>
                )}

                {this.state.currentQuestion.answerType === "text" && (
                    <TextAnswer answerLabel="Address" value={this.state.question1AddressValue} handleTextResponse={this.handleQuestion1Address}></TextAnswer>
                )}

                <TextAnswer answerLabel="Address" value={this.state.question1AddressValue} handleTextResponse={this.handleQuestion1Address}></TextAnswer>
                <TextAnswer answerLabel="City" value={this.state.question1CityValue} handleTextResponse={this.handleQuestion1City}></TextAnswer>
                <TextAnswer answerLabel="County" value={this.state.question1CountyValue} handleTextResponse={this.handleQuestion1County}></TextAnswer>
                <TextAnswer answerLabel="State" value={this.state.question1StateValue} handleTextResponse={this.handleQuestion1State}></TextAnswer>
                <NextButton for="question1" handleNextButton={this.handleNextButtonQ1}></NextButton>
                
            </div>






        )
    }
}

export default Survey
