import React, { Component } from 'react'

class Question1 extends Component {
    
    constructor(props) {
        super(props)
    
        this.state = {
            questionNum: 1,
            questionContent: "Have any papers been filed in this matter?",
            answerType: "multchoice",
            defaultAnswerValue: "",
            backgroundcolor: ""
        }
    }
    
    
    renderQuestion = (questionString) =>{
        return questionString;
    }
    
    onOption1 = (event) => {
        event.preventDefault();
        this.props.handleQ1ChoiceResponse(event.target.value);
        
        this.setState({
            backgroundcolor: "green"
        })

    }

    onOption2 = (event) => {
        event.preventDefault();
        this.props.handleQ1ChoiceResponse(event.target.value);
        
        this.setState({
            backgroundcolor: "green"
        })

    }

    render() {
        return (
            <div>
                <div>
                    {this.renderQuestion(this.state.questionContent)}
                </div>
                <div>
                    <button className="q1-option1" style={{backgroundColor: this.state.backgroundcolor}} value="YES" onClick={this.onOption1}>YES</button>
                    <button className="q1-option2" style={{backgroundColor: this.state.backgroundcolor}} value="NO" onClick={this.onOption2}>NO</button>
                </div>
            </div>
        )
    }
}

export default Question1
