import React, { Component } from 'react'

class AddClients extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             nameField: "",
             addressField: "",
             cityField: "",
             telephoneField: "",
             emailField: "",
             showAddClient: false,
        }
    }

    handleNameInput = (event) => {
        this.setState({
            nameField: event.target.value
        })
    }

    handleAddressInput = (event) => {
        this.setState({
            addressField: event.target.value
        })
    }

    handleCityInput = (event) => {
        this.setState({
            cityField: event.target.value
        })
    }

    handleTelephoneInput = (event) => {
        this.setState({
            telephoneField: event.target.value
        })
    }
    
    handleEmailInput = (event) => {
        this.setState({
            emailField: event.target.value
        })
    }
    
    handleBtn = (event) => {
        console.log(this.state);
    }

    addClient = (event) => {
        if(this.state.showAddClient){
            this.setState({
                showAddClient: false
            })
        }
        else{
            this.setState({
                showAddClient: true
            })
        }
    }

    render() {
        return (
            <div>
                <div>
                    <h1>Clients</h1>
                    <button onClick={this.addClient}>Add Client</button>
                    
                    <label>Name</label>
                    <label>Street Address</label>
                    <label>City</label>
                    <label>Telephone Number</label>
                    <label>Email Address</label>

                    {this.state.showAddClient && (
                        <div className="add-client-container">
                            <div>Name</div>
                            <input type="text" onChange = {this.handleNameInput}></input>

                            <label>Street Address</label>
                            <input type="text" onChange = {this.handleAddressInput}></input>

                            <label>City</label>
                            <input type="text" onChange = {this.handleCityInput}></input>

                            <label>Telephone Number</label>
                            <input type="text" onChange = {this.handleTelephoneInput}></input>

                            <label>Email Address</label>
                            <input type="text" onChange = {this.handleEmailInput}></input>

                            <button onClick={this.handleBtn}>btn</button>     
                        </div>
                    )}
                </div>
            </div>
        )
    }
}

export default AddClients
