import React, { Component } from 'react'
import { Link } from 'react-router-dom';
import {BrowserRouter as Router, Switch, Route, Redirect} from 'react-router-dom';
import Form from './Form';

class Home extends Component {
    
    constructor(props) {
        super(props)
    
        this.state = {
             isLoading: true,
             user: null,
             error: null
        }
    }
    
    componentDidMount = () =>{
        fetch("http://localhost:3001/account", {
            method: "GET",
            credentials: "include",
        }).then((res) => {
            return res.json();
        }).then((parsedResponse) => {
            if(parsedResponse.error){
                this.setState({
                    error: parsedResponse.error,
                    isLoading: false
                })
                console.log("user not logged in")
            }
            else{
                this.setState({
                    user: parsedResponse.user,
                    isLoading: false
                })
            }
        })
        .catch(() => {
            this.setState({
                error: "ERROR",
                isLoading: false
            })
        })
    }

    
    render() {

        if(this.state.isLoading){
            return(
                <div>Loading...</div>
            )
        }

        if(this.state.error){
            return(
                <Redirect to="/login"></Redirect>
            )
        }

        return (
            <div>
                <div className="nav-bar-container">
                    <div className="nav-bar-links-container">
                        <Link to='/login' className="nav-bar-links">
                            Login
                        </Link>
                        <Link to='/register' className="nav-bar-links">
                            Register
                        </Link>
                        <Link to='/passwordreset' className="nav-bar-links">
                            Forgot Password?
                        </Link>
                        <Link to='/home/form' className="nav-bar-links">
                            Form
                        </Link>
                    </div>
                    

                    <div className="nav-bar-account">{this.state.user.name}</div>
                </div>
        
                <div>
                    <Switch>
                        <Route path="/home/form" exact><Form user={this.state.user}></Form></Route>
                    </Switch>
                </div>
            </div>
        )
    }
}

export default Home
