import React, { Component } from 'react'

class Question1 extends Component {
    
    constructor(props) {
        super(props)
    
        this.state = {
            questionNum: 1,
            questionContent: "Have any papers been filed in this matter?",
            answerType: "multchoice",
            defaultAnswerValue: "",
            backgroundcoloroption1: "",
            backgroundcoloroption2: ""
        }
    }
    
    
    renderQuestion = (questionString) =>{
        return questionString;
    }
    
    onOption1 = (event) => {
        event.preventDefault();
        
        this.setState({
            backgroundcoloroption1: "green",
            backgroundcoloroption2: ""
        })


    }

    onOption2 = (event) => {
        event.preventDefault();
        
        this.setState({
            backgroundcoloroption2: "green",
            backgroundcoloroption1: ""
        })

    }

    render() {
        return (
            <div>
                <div>
                    {this.renderQuestion(this.state.questionContent)}
                </div>
                <div>
                    <button className="q1-option1" style={{backgroundColor: this.state.backgroundcoloroption1}} value="YES" onClick={this.onOption1}>YES</button>
                    <button className="q1-option2" style={{backgroundColor: this.state.backgroundcoloroption2}} value="NO" onClick={this.onOption2}>NO</button>
                </div>
                <div>
                    <button type="submit" id="next-question-btn">Next Question</button>
                </div>
            </div>
        )
    }
}

export default Question1
