import React, { Component } from 'react'



class PasswordReset extends Component {
    
    constructor(props) {
        super(props)
    
        this.state = {
             
        }
    }
    
    
    render() {
        return (
            <div>
                <h1>Forgot Your Password?</h1>
                <div>
                    {this.state.errorMessage}
                </div>
                <form onSubmit={this.handleRegistration}>
                    {/* Email Registration */}
                    <div>
                        <label htmlFor="pwResetEmailInput" className = "form-labels">Email Address</label>
                        <input id="pwResetEmailInput" type="text" value={this.state.emailField} onChange = {this.handleEmailRegistration}></input>
                    </div>
            
                    {/* Submit Button */}
                    <div className="submit-button-pwreset">
                        <button type="submit">Submit</button>
                    </div>
                </form>
            </div>
        )
    }
}

export default PasswordReset
