import React, { Component } from 'react'


const allClients = {

}

const addClientFields = {
    "nameField": "",
    "addressField": "",
    "cityField": "",
    "telephoneField": "",
    "emailField": "",
};

class AddClients extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             showAddClient: false,
        }
    }

    showAddClient = (event) => {
        if(this.state.showAddClient){
            this.setState({
                showAddClient: false
            })
        }
        else{
            this.setState({
                showAddClient: true
            })
        }
    }

    handleNameInput = (event) => {
        addClientFields["nameField"] = event.target.value;
    }

    handleAddressInput = (event) => {
        addClientFields["addressField"] = event.target.value;
    }

    handleCityInput = (event) => {
        addClientFields["cityField"] = event.target.value;
    }

    handleTelephoneInput = (event) => {
        addClientFields["telephoneField"] = event.target.value;
    }
    
    handleEmailInput = (event) => {
        addClientFields["emailField"] = event.target.value;
    }
    
    addNewClient = (event) => {
        console.log(addClientFields);

        let sizeofClientList = Object.keys(allClients).length;
        console.log(sizeofClientList);

        allClients[`client${sizeofClientList + 1}`] = {
            name: addClientFields["nameField"],
            address: addClientFields["addressField"],
            city: addClientFields["cityField"],
            phone: addClientFields["telephoneField"],
            email: addClientFields["emailField"],
        };
        console.log(allClients.client1);

    }

    render() {
        return (
            <div>
                <div>
                    <button onClick={this.showAddClient}>Add Client</button>

                    {this.state.showAddClient && (
                        <div>
                        <div className="addclient-container">
                            <div className="label-input-container">
                                <label>Name</label>
                                <input type="text" className="addclient-input-fields" onChange = {this.handleNameInput}></input>
                            </div>
                            <div className="label-input-container">
                                <label>Street Address</label>
                                <input type="text" className="addclient-input-fields" onChange = {this.handleAddressInput}></input>
                            </div>
                            <div className="label-input-container">
                                <label>City</label>
                                <input type="text" className="addclient-input-fields" onChange = {this.handleCityInput}></input>
                            </div>
                            <div className="label-input-container">
                                <label>Telephone Number</label>
                                <input type="text" className="addclient-input-fields" onChange = {this.handleTelephoneInput}></input>
                            </div>
                            <div className="label-input-container">
                                <label>Email Address</label>
                                <input type="text" className="addclient-input-fields" onChange = {this.handleEmailInput}></input>
                            </div>
   
                        </div>
                        <button onClick={this.addNewClient} id="addclient-btn">Submit</button>
                        </div>  
                    )}
                </div>
            </div>
        )
    }
}

export default AddClients
