import React, { Component } from 'react'

class TextAnswer extends Component {
    
    constructor(props) {
        super(props)
    
        this.state = {
             
        }

        this.onValueChange = this.onValueChange.bind(this);
    }
    
    onValueChange(e) {
        this.props.handleTextResponse(e.value);
    }

    render() {
        return (
            <div>
                <div>
                    <label htmlFor="textanswer" style={{display: "none"}}>{this.props.answerLabel}</label>
                    <input className="textanswer" type="text" value={this.props.value} onInput={this.onValueChange.bind(this)}></input>
                </div>
            </div>
        )
    }
}

export default TextAnswer
