import React, { Component } from 'react'

const allClients = {
    
};

class ClientListRow extends Component {
   
    constructor(props) {
        super(props)
    
        this.state = {
             currentClient: this.props.currentClient,
        }
    }

   
    handleNameInput = (event) => {
        
    }


    render() {
        const {currentClient} = this.state;
        const currentFields = this.props.allClients[currentClient].name
        return (
            <div className="client-list-container">
                <div className="label-input-container">
                    <input type="text" className="addclient-input-fields" value={currentFields} onChange={this.handleNameInput}></input>
                </div>
                <div className="label-input-container">
                    <input type="text" className="addclient-input-fields" onChange = {this.handleAddressInput}></input>
                </div>
                <div className="label-input-container">
                    <input type="text" className="addclient-input-fields" onChange = {this.handleCityInput}></input>
                </div>
                <div className="label-input-container">
                    <input type="text" className="addclient-input-fields" onChange = {this.handleTelephoneInput}></input>
                </div>
                <div className="label-input-container">
                    <input type="text" className="addclient-input-fields" onChange = {this.handleEmailInput}></input>
                </div>
            </div>
        )
    }
}

export default ClientListRow
