import React, { Component } from 'react'



class PasswordReset extends Component {
    
    constructor(props) {
        super(props)
    
        this.state = {
             
        }
    }
    
    
    render() {
        return (
            <div>
                <h1>Forgot Your Password?</h1>
                <div>
                    {this.state.errorMessage}
                </div>
                <form onSubmit={this.handleRegistration}>
                    {/* Email Registration */}
                    <div>
                        <label htmlFor="registeremailinput" className = "form-labels">Email Address</label>
                        <input id="registeremailinput" type="text" value={this.state.emailField} onChange = {this.handleEmailRegistration}></input>
                    </div>
                    
                    {/* Name Registration */}
                    <div>
                        <label htmlFor="registernameinput" className = "form-labels">Name</label>
                        <input id="registernameinput" type="text" value={this.state.nameField} onChange = {this.handleNameRegistration}></input>
                    </div>

                    {/* Password Registration */}
                    <div>
                        <label htmlFor ="registerpassword" className = "form-labels">Password</label>
                        <input id="registerpassword" type="password" value={this.state.passwordField} onChange={this.handlePasswordRegistration}></input>
                    </div>
                    
                    {/* Submit Button */}
                    <div>
                        <button type="submit">Submit</button>
                    </div>
                </form>
            </div>
        )
    }
}

export default PasswordReset
